mapper.update:
update alarm
set checked = true,
    modified_at = now()
where checked = false
  and user_id = #{userId}
  and alarm_type = #{alarmType}
  and table_id = #{tableId}

mapper.select:
select alarm_id, user_id, alarm_type, checked, table_id, created_at
from alarm
where user_id = #{userId}
  and alarm_id < #{scrollRequest.key}
order by created_at desc limit #{scrollRequest.size}

select ifnull(count(case when checked ='false' then 1 end), 0) as count
from alarm
where user_id = #{userId}

select alarm_id, user_id, alarm_type, checked, table_id, created_at
from alarm
where alarm_id = #{id}

select alarm_id, user_id, alarm_type, checked, table_id, created_at
from alarm
where user_id = #{userId}
order by created_at desc limit #{scrollRequest.size}
