mapper.select:
SELECT count(*)
  FROM qna_comment
 WHERE qna_id = #{qnaId}

mapper.delete:
DELETE
  FROM qna_comment
 WHERE id = #{id}
   AND user_id = #{userId}

mapper.select:
SELECT id, created_at, modified_at, is_delete, comment, user_id, qna_id
FROM qna_comment
WHERE id = #{id}

mapper.update:
UPDATE qna_comment
SET comment = #{comment}
  , modified_at =#{modifiedAt}
WHERE id = #{id} and user_id = #{userId}
