mapper.select:
SELECT f.*, u.nick_name as user_nick_name FROM feed f INNER JOIN (
SELECT f.feed_id, f.content, f.user_id, ROW_NUMBER() OVER(PARTITION BY f.user_id ORDER BY f.created_at DESC) AS row_num FROM feed f JOIN feed_like_count flc ON f.feed_id = flc.feed_id WHERE f.is_delete = false AND f.user_id IN ( SELECT f.following_user_id FROM user u JOIN follow f ON u.user_id = #{userId} AND u.user_id = f.follower_user_id )
) AS following_users_feeds ON following_users_feeds.feed_id = f.feed_id INNER JOIN user u ON u.user_id = f.user_id ORDER BY f.created_at DESC LIMIT 10

SELECT f.*, u.nick_name as user_nick_name FROM feed f JOIN feed_like_count flc ON f.feed_id = flc.feed_id INNER JOIN user u ON u.user_id = f.user_id WHERE f.is_delete = false AND f.feed_id NOT IN
#{feedId}
ORDER BY flc.like_count DESC, f.created_at DESC LIMIT #{size};

mapper.update:
update feed set title = #{request.title}, species = #{request.species}, nickname = #{request.nickname}, temperature = #{request.temperature},
                humidity = #{request.humidity}, water_amount = #{request.waterAmount}, watering_period = #{request.wateringPeriod},
                content = #{request.content}, feed_type = #{request.feedType}, modified_at = now() where feed_id = #{feedId} and user_id = #{userId}
