mapper.update:
UPDATE notice
SET   view_cnt = view_cnt + 1
WHERE noitce_id = #{noitceId}

UPDATE notice
   SET modified_at = now()
     , contents = #{contents}
     , is_hide = #{isHide}
     , title = #{title}
WHERE notice_id = #{noitceId}

mapper.select:
SELECT table_id
FROM alarm
WHERE user_id = #{userId}
LIMIT #{offset}, #{pageSize}
