mapper.update:
UPDATE notice
SET is_delete = 1
WHERE notice_id = #{noticeId}

mapper.select:
SELECT notice_id, created_at
FROM notice
WHERE contents = #{noticeSaveRequest.contents}
  AND title = #{noticeSaveRequest.title}
  AND user_id = #{userId}

mapper.update:
UPDATE notice
SET   view_count = view_count + 1
WHERE notice_id = #{noticeId}

UPDATE notice
   SET modified_at = now()
     , contents = #{noticeEditRequest.contents}
     , title = #{noticeEditRequest.title}
WHERE notice_id = #{noticeId}

mapper.select:
SELECT  modified_at
FROM notice
WHERE contents = #{contents}
  AND title = #{title}

mapper.update:
UPDATE notice
SET   is_hide = 1
WHERE notice_id = #{noticeId}

mapper.select:
SELECT *
FROM notice

ORDER BY ${pageRequest.sortColumn} ${pageRequest.sortStatus}

LIMIT #{pageRequest.limit}
OFFSET #{pageRequest.offset}
