mapper.update:
UPDATE notice
SET   title = #{title}
  , contents = #{contents}
  , modified_at = now()
  , user_id = #{user_id}
WHERE user_id = #{user_id}

UPDATE qna_reply
SET   qnareply_cnt = qnareply_cnt + #{cnt}
WHERE id = #{id}

UPDATE notice
SET   comment_cnt = comment_cnt + #{cnt}
WHERE id = #{id}
