mapper.update:
UPDATE  qna_comment
   SET is_delete = true
 WHERE qna_comment_id = #{qnaCommentId}
   AND user_id = #{userId}
   AND qna_id = #{qnaId}

mapper.select:
SELECT qna_comment_id, created_at, qna_id
FROM qna_comment
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT  1;

mapper.update:
UPDATE qna_comment
SET comment = #{qnaCommentEditRequest.comment}
  , modified_at = now()
WHERE qna_comment_id = #{qnaCommentId}

mapper.select:
SELECT  modified_at
FROM qna_comment
WHERE user_id = #{userId}
ORDER BY modified_at DESC
LIMIT 1;

mapper.update:
UPDATE qna
SET   qna_status = "답변 완료"
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT user_id
FROM qna
WHERE qna_id = #{qnaId}
