mapper.update:
UPDATE  qna_comment
   SET is_delete = true
 WHERE qna_comment_id = #{qnaCommentId}
   AND user_id = #{userId}
   AND qna_id = #{qnaId}
   AND is_delete = false;

mapper.select:
SELECT *
  FROM qna_comment
 WHERE qna_comment_id = #{qnaCommentId}
   AND is_delete = false;

SELECT qna_comment_id, created_at, qna_id
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}

mapper.update:
UPDATE qna_comment
SET comment = #{qnaCommentEditRequest.comment}
  , modified_at = now()
WHERE qna_comment_id = #{qnaCommentId}
  AND user_id = #{userId}
  AND qna_id =  #{qnaId}
  AND is_delete = false;

mapper.select:
SELECT  modified_at
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}

mapper.update:
UPDATE qna
SET   qna_status = #{qnaStatus.text}
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT user_id
FROM qna
WHERE qna_id = #{qnaId}

SELECT qc.*
FROM qna_comment qc
WHERE qc.qna_id = #{qnaId}
  AND qc.is_delete = false;
