mapper.update:
UPDATE  qna_comment
   SET is_delete = true
 WHERE qna_comment_id = #{qnaCommentId}
   AND user_id = #{userId}
   AND qna_id = #{qnaId}

mapper.insert:
INSERT INTO qna_comment
    (created_at, modified_at, is_delete, comment, user_id, qna_id)
SELECT
    now(), now(), 0, #{qnaCommentSaveRequest.comment}, #{userId}, #{qnaId}
FROM
    qna q
WHERE q.qna_id = #{qnaId}
  AND q.is_delete = false;

mapper.select:
SELECT qna_comment_id, created_at, qna_id
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}
ORDER BY created_at DESC
LIMIT  1;

mapper.update:
UPDATE qna_comment
SET comment = #{qnaCommentEditRequest.comment}
  , modified_at = now()
WHERE qna_comment_id = #{qnaCommentId}
  AND user_id = #{userId}
  AND qna_id =  #{qnaId}

mapper.select:
SELECT  modified_at
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}

mapper.update:
UPDATE qna
SET   qna_status = #{defaultStatus.text}
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT user_id
FROM qna
WHERE qna_id = #{qnaId}

SELECT qc.*
FROM qna_comment qc
WHERE qc.qna_id = #{qnaId}
  AND qc.is_delete = false;
