mapper.update:
UPDATE  qna_comment
   SET is_delete = true
 WHERE qna_comment_id = #{qnaCommentId}

mapper.select:
SELECT qna_comment_id, created_at, qna_id
FROM qna_comment
WHERE comment = #{qnaCommentSaveRequest.comment}
  AND user_id = #{userId}

mapper.update:
UPDATE qna_comment
SET comment = #{comment}
  , modified_at = now()
WHERE qna_comment_id = #{qnaCommentId}

mapper.select:
SELECT  modified_at
FROM qna_comment
WHERE comment = #{qnaCommentSaveRequest.comment}
