mapper.update:
UPDATE qna_reply
   SET is_delete = true
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
   AND qna_comment_id =${qnaCommentId}

mapper.select:
SELECT qna_reply_id, created_at
FROM qna_reply
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT  1;

mapper.update:
UPDATE qna_reply
   SET  modified_at = now()
       ,comment = #{qnaReplyEditReqeust.comment}
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
   AND qna_comment_id =${qnaCommentId}

mapper.select:
SELECT  modified_at
FROM qna_reply
WHERE user_id = #{userId}
ORDER BY modified_at DESC
LIMIT 1;

SELECT user_id
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}
