mapper.update:
UPDATE qna_reply
   SET is_delete = 1
 WHERE qna_reply_id = #{qnaReplyId}

mapper.select:
SELECT qna_reply_id, created_at
FROM qna
WHERE contnets = #{contnets}
  AND title = #{title}
  AND user_id = #{userId}

mapper.update:
UPDATE qna_reply
   SET  modified_at = now()
       ,comment = #{comment}
 WHERE qna_reply_id = #{qnaReplyId}
