mapper.select:
SELECT count(*)
  FROM qna_reply
 WHERE qna_reply_id = #{qnaReplyId}

mapper.update:
UPDATE qna_reply
   SET is_delete = 1
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}

UPDATE qna_reply
   SET  modified_at = now()
       ,comment = #{comment}
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
