mapper.update:
UPDATE qna_reply
   SET is_delete = true
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
   AND qna_comment_id =${qnaCommentId}

mapper.insert:
INSERT INTO qna_reply
    ( created_at, modified_at, is_delete, comment, user_id, qna_comment_id)
SELECT
     now(), now(),  0, #{qnaReplySaveRequest.comment}, #{userId}, #{qnaCommentId}
FROM
    qna_comment qc
WHERE qc.qna_comment_id = #{qnaCommentId}
AND qc.is_delete = false;

mapper.select:
SELECT qna_reply_id, created_at
FROM qna_reply
WHERE qna_reply_id = #{qnaReplyId}
ORDER BY created_at DESC
LIMIT  1;

mapper.update:
UPDATE qna_reply
   SET  modified_at = now()
       ,comment = #{qnaReplyEditReqeust.comment}
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
   AND qna_comment_id =${qnaCommentId}

mapper.select:
SELECT  modified_at
FROM qna_reply
WHERE qna_reply_id = #{qnaReplyId}
ORDER BY modified_at DESC
LIMIT 1;

SELECT user_id
FROM qna_comment
WHERE qna_comment_id = #{qnaCommentId}

SELECT qr.*
FROM qna_reply qr
WHERE qr.qna_comment_id IN (
    SELECT qc.qna_comment_id
    FROM qna_comment qc
    WHERE qc.qna_id = #{qnaId}
) AND qr.is_delete = false;
