mapper.update:
UPDATE qna_reply
   SET is_delete = true
 WHERE qna_reply_id = #{qnaReplyId}

mapper.select:
SELECT qna_reply_id, created_at
FROM qna
WHERE contnets = #{contnets}

mapper.update:
UPDATE qna_reply
   SET  modified_at = now()
       ,comment = #{comment}
 WHERE qna_reply_id = #{qnaReplyId}

mapper.select:
SELECT  modified_at
FROM qna_reply
WHERE comment = #{qnaReplyEditResponse.comment}
