mapper.select:
SELECT count(*)
  FROM qna_reply
 WHERE qna_reply_id = #{qnaReplyId}

mapper.delete:
DELETE FROM qna_reply
       WHERE qna_reply_id = #{qnaReplyId}
         AND user_id = #{userId}

mapper.update:
UPDATE qna_reply
   SET user_id = #{userId}
     , modified_at = now()
 WHERE qna_reply_id = #{qnaReplyId}
   AND user_id = #{userId}
