mapper.update:
UPDATE qna
SET is_delete = 1
WHERE qna_id = #{qnaId}

mapper.select:
SELECT qna_id, created_at
FROM qna
WHERE contnets = #{contnets}
  AND title = #{title}
  AND user_id = #{userId}

mapper.update:
UPDATE qna
SET   view_count = view_count + 1
WHERE qna_id = #{qnaId}

UPDATE qna
SET contents    = #{contents}
  , title       = #{title}
  , modified_at = now()
  , user_id     = #{userId}
WHERE qna_id      = #{qnaId}

UPDATE qna
SET   qna_status = "답변 완료"
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT *
FROM qna
WHERE qna_id = #{qnaId}
  AND user_id #{userId}

