SET is_delete = true
WHERE qna_id = #{qnaId}

mapper.select:
SELECT qna_id, created_at
FROM qna
WHERE contents = #{qnaSaveRequest.contents}
AND title = #{qnaSaveRequest.title}
AND user_id = #{userId}

mapper.update:
