mapper.select:
SELECT *
FROM qna
WHERE user_id = #{userId}
AND is_delete = false

    ORDER BY #{pageRequest.sortColumn} #{pageRequest.sortStatus}


    ORDER BY modified_at desc, created_at desc

LIMIT #{pageRequest.limit}
OFFSET #{pageRequest.offset}

