mapper.delete:
UPDATE qna
SET is_delete = 1
WHERE qna_id = #{qnaId}

mapper.update:
UPDATE qna
SET   view_cnt = view_cnt + 1
WHERE qna_id = #{qnaId}

UPDATE qna
SET   title = #{title}
  , contents = #{contents}
  , modified_at = now()
  , user_id = #{userId}
WHERE qna_id = #{qnaId}

UPDATE qna
SET   qna_status = 1
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT *
FROM qna
WHERE qna_id = #{qnaId}
ORDER BY ${pageRequest.sortColumn} ${pageRequest.sortStatus}
LIMIT #{pageRequest.limit}
OFFSET #{pageRequest.offset}
