mapper.update:
UPDATE qna
SET is_delete = 1
WHERE qna_id = #{qnaId}

UPDATE qna
SET   view_count = view_count + 1
WHERE qna_id = #{qnaId}

UPDATE qna
SET contents    = #{qnaDto.contents}
  , title       = #{qnaDto.title}
  , modified_at = now()
  , user_id     = #{qnaDto.userId}
WHERE qna_id      = #{qnaId}

UPDATE qna
SET   qna_status = "답변 완료"
WHERE  qna_id = #{qnaId}

mapper.select:
SELECT *
FROM qna
WHERE qna_id = #{qnaId}
ORDER BY ${pageRequest.sortColumn} ${pageRequest.sortStatus}
LIMIT #{pageRequest.limit}
OFFSET #{pageRequest.offset}
