mapper.delete:
UPDATE qna
SET is_delete = 1
WHERE id = #{id} AND user_id = #{userId}

mapper.update:
UPDATE qna
SET   view_cnt = view_cnt + 1
WHERE id = #{id}

UPDATE qna
SET   title = #{title}
  , contents = #{contents}
  , modified_at = now()
  , user_id = #{userId}
WHERE id = #{id}

UPDATE qna
SET   qna_status = 1
WHERE  id = #{id}
