mapper.select:
SELECT count(*)
FROM qna_reply
WHERE qna_id = #{qnaId}

SELECT id, created_at, modified_at, is_delete, comment, user_id, qna_comment_id
FROM qna_reply
WHERE id = #{id}

mapper.update:
UPDATE qna_reply
SET user_id = #{userId}
  , modified_at = now()
WHERE id = #{id} and user_id = #{userId}
